import {
  ExceptionFilter,
  Catch,
  ArgumentsHost,
  HttpException,
} from '@nestjs/common';
import { Request, Response } from 'express';

@Catch(HttpException)
export class HttpExceptionFilter implements ExceptionFilter {
  catch(exception: HttpException, host: ArgumentsHost) {
    const ctx = host.switchToHttp();
    const response = ctx.getResponse<Response>();
    const request = ctx.getRequest<Request>();
    const status = exception.getStatus();

    response.status(status).json({
      succeeded: false,
      message: exception.message || 'An error occurred',
      statusCode: status,
      timestamp: new Date().toISOString(),
      path: request.url,
    });
  }
}

import {
  CallHandler,
  ExecutionContext,
  Injectable,
  NestInterceptor
} from '@nestjs/common';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

export interface Response<T> {
  data: T;
}

@Injectable()
export class ResponseInterceptor<T> implements NestInterceptor<T, Response<T>> {
  intercept(
    context: ExecutionContext,
    next: CallHandler,
  ): Observable<Response<T>> {
    return next.handle().pipe(
      map((value: any) => {
        
        let totalRecords = undefined;
        if (Array.isArray(value.data) && value.totalRecords) {
          totalRecords = value?.totalRecords ? value.totalRecords : 0;
        }

        return {
          succeeded: true,
          totalRecords,
          statusCode:
            value?.status && typeof value.status === 'number'
              ? value.status
              : 201,
          message: value?.message ? value.message : 'Successful',
          data:
            (value?.totalRecords !== null &&
              value?.totalRecords !== undefined &&
              typeof value?.totalRecords === 'number') ||
            value?.data
              ? value.data
              : value?.message
                ? []
                : value,
        };
      })
    );
  }
}
